import { Component } from '@angular/core';
import { FormControl, FormGroup , Validators} from '@angular/forms';

@Component({
  selector: 'app-content-form',
  templateUrl: './content-form.component.html',
  styleUrls: ['./content-form.component.css']
})
export class ContentFormComponent {

  formSubscription: FormGroup;

  constructor(){
    let controls: any = {
      name_: new FormControl('', [ Validators.required, Validators.minLength(3)  ]),
      lastName_: new FormControl('', [ Validators.required, Validators.minLength(3)  ]),
      email_: new FormControl('', [ Validators.required , Validators.email  ]),
    }

    this.formSubscription = new FormGroup(controls);

  }

  handleSubscription(){

  }

}
<form class="ic-dynamic-form" (ngSubmit)="handleSubscription()">
  <div class="mb-2">
      <label class="mb-1" for="firstname-input">Name:</label>
      <input value="" type="text" id="firstname-input" name="firstname-input" class="form-control" placeholder="*Name">
  </div>
  <div class="mb-2">
    <label class="mb-1" for="lastname-input">Last name:</label>
    <input value="" type="text" id="lastname-input" name="lastname-input" class="form-control" placeholder="*Last name">
  </div>
  <div class="mb-2">
      <label class="mb-1" for="email-input">Email:</label>
      <input value="" type="text" id="email-input" name="email-input" class="form-control" placeholder="*Email">
  </div>
  <div class="mt-4 mb-2">
      <button type="submit" class="btn w-100">Send</button>
  </div>
</form>
