import { Component } from '@angular/core';
import { FormControl, FormGroup , Validators} from '@angular/forms';

@Component({
  selector: 'app-content-form',
  templateUrl: './content-form.component.html',
  styleUrls: ['./content-form.component.css']
})
export class ContentFormComponent {

  formSubscription: FormGroup;

  constructor(){
    let controles: any = {
      name_: new FormControl('', [ Validators.required , Validators.minLength(2) ]),
      lastName_: new FormControl('', [ Validators.required, Validators.minLength(3)   ]),
      email_: new FormControl('', [ Validators.required , Validators.email  ]),
    }

    this.formSubscription = new FormGroup(controles);

  }

  handleSubscription(){
    console.log('login', this.formSubscription);
  }

}
<form class="ic-dynamic-form"  [formGroup]="formSubscription" (ngSubmit)="handleSubscription()">
  <div class="mb-2">
      <label class="mb-1" for="firstname-input">Name:</label>
      <input value="" type="text" id="firstname-input" name="firstname-input" class="form-control" placeholder="*Name" formControlName="name_">
      <!--check-->
      <div class="valid" *ngIf="formSubscription.controls['lastName_'].dirty && formSubscription.controls['lastName_'].valid" >accepted</div>
      <div class="invalid" *ngIf="formSubscription.controls['lastName_'].dirty && formSubscription.controls['lastName_'].errors?.['required']" >required field</div>
      <div class="invalid" *ngIf="formSubscription.controls['lastName_'].dirty && formSubscription.controls['lastName_'].errors?.['minlength']">insert more characters (min.2) </div>
  </div>
  <div class="mb-2">
    <label class="mb-1" for="lastname-input">Last name:</label>
    <input value="" type="text" id="lastname-input" name="lastname-input" class="form-control" placeholder="*Last name" formControlName="lastName_">
    <!--check-->
    <div class="valid" *ngIf="formSubscription.controls['lastName_'].dirty && formSubscription.controls['lastName_'].valid" >accepted</div>
    <div class="invalid" *ngIf="formSubscription.controls['lastName_'].dirty && formSubscription.controls['lastName_'].errors?.['required']" >required field</div>
    <div class="invalid" *ngIf="formSubscription.controls['lastName_'].dirty && formSubscription.controls['lastName_'].errors?.['minlength']">insert more characters (min.3) </div>
  </div>
  <div class="mb-2">
      <label class="mb-1" for="email-input">Email:</label>
      <input value="" type="text" id="email-input" name="email-input" class="form-control" placeholder="*Email" formControlName="email_">
      <!--check-->
      <div class="valid" *ngIf="formSubscription.controls['email_'].dirty && formSubscription.controls['email_'].valid" >accepted</div>
      <div class="invalid" *ngIf="formSubscription.controls['email_'].dirty && formSubscription.controls['email_'].errors?.['required']" >required field</div>
      <div class="invalid" *ngIf="formSubscription.controls['email_'].dirty && formSubscription.controls['email_'].errors?.['email']">invalid e-mail</div>
  </div>
  <div class="mt-4 mb-2">
      <button type="submit" class="btn w-100" [disabled]="formSubscription.invalid">Send</button>
  </div>
</form>
