import { Component } from '@angular/core';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent {

}
<nav class="d-flex align-items-center sticky-top">
  <div class="container">
      <div class="row justify-content-center justify-content-between">
          <div class="col pr-0 pl-md-0">
            <img src="../../../assets/img/logo.png" width="100" alt="logo">
          </div>
          <div class="col pl-0 d-flex justify-content-end align-items-center text-right">
              <p class="d-flex nav-text">Your Coding School.</p>
          </div>
      </div>
  </div>
</nav>
